// stopwords-regex.js
module.exports = regexes = (function () {

	var stopwords= {
		ar: require('./stopwords-ar.js'),
		bg: require('./stopwords-bg.js'),
		cs: require('./stopwords-cs.js'),
		da: require('./stopwords-da.js'),
		de: require('./stopwords-de.js'),
		el: require('./stopwords-el.js'),
		en: require('./stopwords-en.js'),
		es: require('./stopwords-es.js'),
		fi: require('./stopwords-fi.js'),
		fr: require('./stopwords-fr.js'),
		hu: require('./stopwords-hu.js'),
		id: require('./stopwords-id.js'),
		it: require('./stopwords-it.js'),
		ko: require('./stopwords-ko.js'),
		nb: require('./stopwords-nb.js'),
		no: require('./stopwords-no.js'),
		pl: require('./stopwords-pl.js'),
		pt: require('./stopwords-pt.js'),
		ru: require('./stopwords-ru.js'),
		sv: require('./stopwords-sv.js'),
		th: require('./stopwords-th.js'),
		tr: require('./stopwords-tr.js'),
		zh: require('./stopwords-zh.js')
	};

	var codes = [ 'ar', 'bg', 'cs', 'da', 'de', 'el', 'en',
				'es', 'fi', 'fr', 'hu', 'id', 'it', 'ko', 'nb', 'no',
				'pl', 'pt', 'ru', 'sv', 'th', 'tr', 'zh' ]

	var regexes = {};

	// Make a regular expression to match all the words in a stopwords file
	for (let codei = 0; codei < codes.length; codei++) {

	 	let code = codes[codei];

		// let escb = JSON.stringify('\b');
		let str = stopwords[ code ].join( '|');//\\b' );
		str = '(' + str + ')';
		// str = '(\\b' + str + ')';
	 	
	 	regexes[ code ] = new RegExp( str, 'g' );
	}

	// console.log('regexes made:', regexes)

	return regexes;
})();

